import React, {Component} from "react";
import Header from "./Header/Header"
import Movie from "./Body/Movie/Movie"

class Layout extends Component{
    state = {
        apiKey:"b2f49c297ee42a377615666c4da25acf",
        storedDate:[]

    }
    inputHandler =  (event) => {

        const searchField = event.target.value
       const movieSearch = "https://api.themoviedb.org/3/search/movie?api_key=" + this.state.apiKey + "&language=en-US&query=" + searchField +"20&page=1&include_adult=false"
           const promise = new Promise((resolve,reject)=>{
            const catchData =  fetch(movieSearch)
            resolve(catchData);
       })
       promise.then((response)=>response.json())
       .then((data)=>{

        const movieDiv = document.getElementById("movieDiv");
        movieDiv.innerHTML=""
        const movieOverView =document.createElement("p")
        const movieTilte = document.createElement("p")
            const filteredMovieData = data.results.filter((movie)=>{
                return movie.title === searchField
            })
            filteredMovieData.forEach((movie)=>{
                movieTilte.innerHTML="Title: " + movie.title
                movieOverView.innerHTML ="Movie Overview: " + movie.overview
                movieDiv.appendChild(movieOverView)

            })
       })



    }
    render(){
        return (
            <div>
            <Header></Header>
            <Movie inputHandler={this.inputHandler}></Movie>
            </div>
        )
    }
}
export default Layout
